    /* lấy số đếm 4 của một lớp */
    function lessonCountFor(cls){ return quizBank[cls] ? Object.keys(quizBank[cls]).length : 0; }

    /* ----------------------
       PHƯƠNG THỨC BÀI HỌC: hiển thị bài học + chọn số câu hỏi
    -----------------------*/
    function showLessonModalForClass(cls){
      selectedClass = cls || 7;
      const lessonCount = lessonCountFor(selectedClass);
      const info = document.getElementById('lesson-modal-info');
      const btns = document.getElementById('lesson-buttons');
      info.innerText = `Lớp ${selectedClass} — chọn 1 trong ${lessonCount} bài:`;
      btns.innerHTML = '';
      for(let i=1;i<=lessonCount;i++){
        const b = document.createElement('button');
        b.textContent = 'Bài ' + i;
        b.style.minWidth = '64px';
        b.onclick = ()=>{ chooseLessonForQuiz(selectedClass, i); };
        btns.appendChild(b);
      }
      // đặt lựa chọn số mặc định
      document.getElementById('num-questions-select').value = String(numQuestionsChoice || 10);
      document.getElementById('lesson-modal').style.display = 'flex';
    }
    function closeLesson(){ document.getElementById('lesson-modal').style.display='none'; }

    /* ----------------------
       Xáo trộn câu
    -----------------------*/
    function shuffle(arr){
      const a = arr.slice();
      for(let i=a.length-1;i>0;i--){
        const j = Math.floor(Math.random()*(i+1));
        [a[i],a[j]] = [a[j],a[i]];
      }
      return a;
    }

    /* ----------------------
       chọn số câu + vào quiz
    -----------------------*/
    function chooseLessonForQuiz(cls, lessonNumber){
      selectedClass = cls;
      selectedLesson = lessonNumber;
      numQuestionsChoice = parseInt(document.getElementById('num-questions-select').value || '10',10);
      closeLesson();
      startQuiz(cls, lessonNumber, numQuestionsChoice);
    }

